<!-- Some styles to get you started. -->

<style>
.search-results {
  z-index: 8889;
  list-style-type: none;
  width: 355px;
  margin: 0;
  padding: 0;
  background: #ffffff;
  border: 1px solid #d4d4d4;
  border-radius: 3px;
  -webkit-box-shadow: 0px 4px 7px 0px rgba(0,0,0,0.1);
  box-shadow: 0px 4px 7px 0px rgba(0,0,0,0.1);
  overflow: hidden;
}
.search-results li {
  display: block;
  width: 100%;
  height: 38px;
  margin: 0;
  padding: 0;
  border-top: 1px solid #d4d4d4;
  line-height: 38px;
  overflow: hidden;
}
.search-results li:first-child {
  border-top: none;
}
.search-results .title {
  float: left;
  width: 215px;
  padding-left: 8px;
  white-space: nowrap;
  overflow: hidden;
  /* The text-overflow property is supported in all major browsers. */
  text-overflow: ellipsis;
  -o-text-overflow: ellipsis;
  text-align: left;
}
.search-results .thumbnail {
  float: left;
  display: block;
  width: 32px;
  height: 32px;
  margin: 3px 0 3px 3px;
  padding: 0;
  text-align: center;
  overflow: hidden;
}
.search-results .price {
  float: left;
  display: block;
  width: 100px;
  height: 32px;
  padding: 0;
  text-align: center;
  overflow: hidden;
}
.search-results a{
  font-size: 13px;
}
 @media (max-width: 940px){
  .search-results{
  	width: 280px;
  }
  .search-results .price{
  	width: 80px;
  }
  .search-results .title {
  	width: 160px;
  }
  .search-results a{
  	font-size: 75%;
  }
}
</style>
